import { Component, OnInit } from '@angular/core';
import { Member } from 'src/app/_models/member';
import { Pagination } from 'src/app/_models/pagination';
import { UserParams } from 'src/app/_models/userParams';
import { MembersService } from 'src/app/_services/members.service';
import { AccountService } from 'src/app/_services/account.service';
import { map } from 'rxjs';

@Component({
  selector: 'app-member-list',
  templateUrl: './member-list.component.html',
  styleUrls: ['./member-list.component.css'],
})
export class MemberListComponent implements OnInit {
  members: Member[] = [];
  pagination: Pagination | undefined;
  userParams: UserParams | undefined;
  genderList = [
    { value: 'male', display: 'Males' },
    { value: 'female', display: 'Females' },
  ];
  canEditDelete = false;

  constructor(
    private accountService: AccountService,
    private membersService: MembersService) {
    this.userParams = this.membersService.getUserParams();
    console.log(this.userParams);
  }

  ngOnInit(): void {
    this.loadMembers();

this.accountService.currentUser$.pipe(
  map((user) => {
    if (!user) return false;
    if (user.roles.includes('Admin') || user.roles.includes('Moderator')) {
      return true;
    } else {
      this.toastr.error('You cannot enter this arae');
      return false;
    }
  })
);
  }

  loadMembers() {
    //console.log(this.userParams);
    if (this.userParams) {
      this.membersService.setUserParams(this.userParams);
      this.membersService.getMembers(this.userParams).subscribe({
        next: (Response) => {
          if (Response.result && Response.pagination) {
            this.members = Response.result;
            this.pagination = Response.pagination;
          }
        },
      });
    }
  }

  resetFilters() {
    this.userParams = this.membersService.resetUserParams();
    this.loadMembers();
  }

  pageChanged(event: any) {
    if (this.userParams && this.userParams?.pageNumber !== event.page) {
      this.userParams.pageNumber = event.page;
      this.membersService.setUserParams(this.userParams);
      this.loadMembers();
    }
  }
}
<div class="text-center mt-3">
    <h2>Total Users - {{pagination?.totalItems}} found</h2>
</div>

<div class="row">
    <table class="table">
        <thead>
            <tr>
                <th scope="col">User Name</th>
                <th scope="col">Display Name</th>
                <th scope="col">First Name</th>
                <th scope="col">Last Name</th>
                <th scope="col" *ngIf="canEditDelete">Edit</th>
                <th scope="col" *ngIf="canEditDelete">Delete</th>
            </tr>
        </thead>
        <tbody>
            <tr *ngFor="let member of members">
                <td>{{member.userName}}</td>
                <td>{{member.displayName}}</td>
                <td>{{member.firstName}}</td>
                <td>{{member.lastName}}</td>
                <td *ngIf="canEditDelete && member.userName != 'admin'">
                    Edit
                </td>
                <td *ngIf="canEditDelete && member.userName != 'admin'">
                    Delete
                </td>
            </tr>
        </tbody>
    </table>

</div>

<div class="d-flex justify-content-center" *ngIf="pagination">
    <pagination 
        [boundaryLinks]="true" 
        [totalItems]="pagination.totalItems"
        [itemsPerPage]="pagination.itemsPerPage"
        [maxSize]="10"
        [(ngModel)]="pagination.currentPage"
        (pageChanged)="pageChanged($event)"
        previousText="&lsaquo;" 
        nextText="&rsaquo;" 
        firstText="&laquo;" 
        lastText="&raquo;">
    </pagination>
</div>
