import { Component, OnInit } from '@angular/core';
import { BsModalRef } from 'ngx-bootstrap/modal';

@Component({
  selector: 'app-useredit-modal',
  templateUrl: './useredit-modal.component.html',
  styleUrls: ['./useredit-modal.component.css'],
})
export class UsereditModalComponent implements OnInit {
  username = '';
  displayName = '';
  firstName = '';
  lastName = '';
  gender = '';
  closeBtnName = '';

  constructor(public bsModalRef: BsModalRef) {}

  ngOnInit(): void {}
}
    <div class="modal-header">
      <h4 class="modal-title pull-left">Edit User - {{username}}</h4>
      <button type="button" class="btn-close close pull-right" aria-label="Close" (click)="bsModalRef.hide()">
        <span aria-hidden="true" class="visually-hidden">&times;</span>
      </button>
    </div>
    <div class="modal-body">
        <form #userEditForm="ngForm" id="userEditForm">
            <div class="mb-3">
                <label style="margin-right: 10px;">I am a: </label>
                <label class="form-check-label">
                    <input 
                        type="radio" 
                        class="form-check-input" 
                        [checked]="gender == ''"
                        value="male" 
                        formControlName="gender"> Male
                </label>
                <label class="form-check-label">
                    <input type="radio" class="form-check-input" value="female" formControlName="gender"> Female
                </label>
            </div>
        </form>
    </div>
    <div class="modal-footer">
      <button type="button" class="btn btn-default" (click)="bsModalRef.hide()">{{closeBtnName}}</button>
    </div>
