import { Component, OnInit } from '@angular/core';
import { BsModalRef } from 'ngx-bootstrap/modal';
import {
  AbstractControl,
  FormBuilder,
  FormControl,
  FormGroup,
  ValidatorFn,
  Validators,
} from '@angular/forms';

@Component({
  selector: 'app-useredit-modal',
  templateUrl: './useredit-modal.component.html',
  styleUrls: ['./useredit-modal.component.css'],
})
export class UsereditModalComponent implements OnInit {
  username = '';
  displayName = '';
  firstName = '';
  lastName = '';
  gender = '';
  closeBtnName = '';

  userEditForm: FormGroup = new FormGroup({});

  genderList = [
    { value: 'male', display: 'Males' },
    { value: 'female', display: 'Females' },
  ];

  constructor(public bsModalRef: BsModalRef, private fb: FormBuilder) {}

  ngOnInit(): void {
    this.initializeForm();
  }

  initializeForm() {
    this.userEditForm = this.fb.group({
      gender: [this.gender],
      username: [this.username, Validators.required],
      displayName: [this.displayName, Validators.required],
      firstName: [this.firstName, Validators.required],
      lastName: [this.lastName, Validators.required],
      password: [
        '',
        [Validators.required, Validators.minLength(4), Validators.maxLength(8)],
      ],
      confirmPassword: [
        '',
        [Validators.required, this.matchValues('password')],
      ],
    });
    this.userEditForm.controls['password'].valueChanges.subscribe({
      next: () =>
        this.userEditForm.controls['confirmPassword'].updateValueAndValidity(),
    });
  }

  matchValues(matchTo: string): ValidatorFn {
    return (control: AbstractControl) => {
      return control.value === control.parent?.get(matchTo)?.value
        ? null
        : { notMatching: true };
    };
  }

  updateMember() {
    const values = this.userEditForm.value;
    //console.log(values);

    this.mem.accountService.register(values).subscribe({
      next: (response) => {
        this.membersService.memberCache = new Map();
        this.router.navigateByUrl('/members');
        //console.log(response);
      },
      error: (error) => {
        this.validationErrors = error;
        console.log(error);
      },
    });
  }



}
    <div class="modal-header">
      <h4 class="modal-title pull-left">Edit User - {{username}}</h4>
      <button type="button" class="btn-close close pull-right" aria-label="Close" (click)="bsModalRef.hide()">
        <span aria-hidden="true" class="visually-hidden">&times;</span>
      </button>
    </div>
    <div class="modal-body">
        <form [formGroup]="userEditForm">
            <div class="mb-3">
                <label class="form-check-label">
                    <input type="radio" class="form-check-input" value="male" formControlName="gender"> Male
                </label>
                <label class="form-check-label">
                    <input type="radio" class="form-check-input" value="female" formControlName="gender"> Female
                </label>
            </div>
            <label>User Name</label>
            <app-text-input 
                [formControl]="$any(userEditForm.controls['username'])"
                [label]="'Username'"
            ></app-text-input>
            <label>Display Name</label>
            <app-text-input 
                [formControl]="$any(userEditForm.controls['displayName'])"
                [label]="'Display Name'"
            ></app-text-input>
            <label>First Name</label>
            <app-text-input 
                [formControl]="$any(userEditForm.controls['firstName'])"
                [label]="'First Name'"
            ></app-text-input>
            <label>Last Name</label>
            <app-text-input 
                [formControl]="$any(userEditForm.controls['lastName'])"
                [label]="'Last Name'"
            ></app-text-input>   
            <hr>
            <app-text-input 
                [formControl]="$any(userEditForm.controls['password'])"
                [label]="'Password'"
                [type]="'password'"
            ></app-text-input>
            <app-text-input 
                [formControl]="$any(userEditForm.controls['confirmPassword'])"
                [label]="'Confirm Password'"
                [type]="'password'"
            ></app-text-input>

        </form>
    </div>
    <div class="modal-footer">
      <button type="button" class="btn btn-default" (click)="bsModalRef.hide()">{{closeBtnName}}</button>
    </div>
