import { Component, OnInit } from '@angular/core';
import { Member } from 'src/app/_models/member';
import { Pagination } from 'src/app/_models/pagination';
import { UserParams } from 'src/app/_models/userParams';
import { MembersService } from 'src/app/_services/members.service';
import { AccountService } from 'src/app/_services/account.service';
import { take } from 'rxjs';
import { User } from '../../_models/user';
import { ToastrService } from 'ngx-toastr';

@Component({
  selector: 'app-member-list',
  templateUrl: './member-list.component.html',
  styleUrls: ['./member-list.component.css'],
})
export class MemberListComponent implements OnInit {
  members: Member[] = [];
  pagination: Pagination | undefined;
  userParams: UserParams | undefined;
  genderList = [
    { value: 'male', display: 'Males' },
    { value: 'female', display: 'Females' },
  ];
  user: User = {} as User;
  canEdit = false;
  canDelete = false;

  constructor(
    private accountService: AccountService,
    private membersService: MembersService,
    private toastr: ToastrService
  ) {
    this.userParams = this.membersService.getUserParams();
    //console.log(this.userParams);
    this.accountService.currentUser$.pipe(take(1)).subscribe({
      next: (user) => {
        if (user) this.user = user;
      },
    });
  }

  ngOnInit(): void {
    this.loadMembers();
    if (this.user != null) {
      var appHasRole = "['Admin', 'Moderator']";
      if (this.user.roles.some((r) => appHasRole.includes(r))) {
        this.canEdit = true;
        this.canDelete = true;
      }
    }
  }

  loadMembers() {
    //console.log(this.userParams);
    if (this.userParams) {
      this.membersService.setUserParams(this.userParams);
      this.membersService.getMembers(this.userParams).subscribe({
        next: (Response) => {
          if (Response.result && Response.pagination) {
            this.members = Response.result;
            this.pagination = Response.pagination;
          }
        },
      });
    }
  }

  resetFilters() {
    this.userParams = this.membersService.resetUserParams();
    this.loadMembers();
  }

  pageChanged(event: any) {
    if (this.userParams && this.userParams?.pageNumber !== event.page) {
      this.userParams.pageNumber = event.page;
      this.membersService.setUserParams(this.userParams);
      this.loadMembers();
    }
  }

  deleteUser(username: string) {
    if (confirm('Are you sure to delete ' + username)) {
      this.accountService.delete(username).subscribe({
        complete: () => {
          //console.log('user (' + username + ') deleted.');
          this.toastr.success(error.error, error.status.toString());

          this.membersService.memberCache = new Map();
          this.loadMembers();

          if (this.user != null) {
            var appHasRole = "['Admin', 'Moderator']";
            if (this.user.roles.some((r) => appHasRole.includes(r))) {
              this.canEdit = true;
              this.canDelete = true;
            }
          }
        },
        error: (error) => {
          this.toastr.error(error.error, error.status.toString());
        },
      });
    }
  }
}
<div class="text-center mt-3">
    <h2>Total Users - {{pagination?.totalItems}} found</h2>
</div>

<div class="row">
    <table class="table">
        <thead>
            <tr>
                <th scope="col">User Name</th>
                <th scope="col">Display Name</th>
                <th scope="col">First Name</th>
                <th scope="col">Last Name</th>
                <th scope="col">Created Date</th>
                <th scope="col">Last Active Date</th>
                <th scope="col" *ngIf="canEdit">Edit</th>
                <th scope="col" *ngIf="canDelete">Delete</th>
            </tr>
        </thead>
        <tbody>
            <tr *ngFor="let member of members">
                <td>{{member.userName}}</td>
                <td>{{member.displayName}}</td>
                <td>{{member.firstName}}</td>
                <td>{{member.lastName}}</td>
                <td>{{member.created | date}}</td>
                <td>{{member.lastActive | date}}</td>
                <td *ngIf="canEdit || member.userName == user.userName ">
                    <button class="btn btn-primary btn-lg me-2" type="button">Edit</button>                    
                </td>
                <td *ngIf="canDelete && member.userName != 'admin'">
                    <button class="btn btn-danger btn-lg me-2" (click)="deleteUser(member.userName)">Delete</button> 
                </td>
            </tr>
        </tbody>
    </table>

</div>

<div class="d-flex justify-content-center" *ngIf="pagination">
    <pagination 
        [boundaryLinks]="true" 
        [totalItems]="pagination.totalItems"
        [itemsPerPage]="pagination.itemsPerPage"
        [maxSize]="10"
        [(ngModel)]="pagination.currentPage"
        (pageChanged)="pageChanged($event)"
        previousText="&lsaquo;" 
        nextText="&rsaquo;" 
        firstText="&laquo;" 
        lastText="&raquo;">
    </pagination>
</div>
